//handling json data 

var customer = {
	name: 'anjan',
	email: 'anjanch1@gmail.com',
	age: 42
}

//display whole json data

console.log(customer);

//display individual json data
console.log("\n");
console.log('Name:' +customer.name);
console.log('Email:' +customer.email);
console.log('Age:' +customer.age);

//edit json data
customer['age']=40;
console.log('Editor age:' + customer.age);


//adding a new json DATA

customer['phone']=123456789;

for(var att in customer)
{
   console.log(att);
   console.log(att+ ":" +customer[att]);


}

//deleting json data

delete customer['age'];
for(var att in customer)
{
	console.log(att);
	console.log(att+ ":" +customer[att]);


}


